import React, { useState } from 'react'
import { uploadFile } from '../services/API'


function FileUploadPage() {
    const [file, setFile] = useState(null)

    const handleFileChange = (event: any) => {
        setFile(event.target.files[0])
    }

    const handleFileUpload = async() => {
        if (file) {
            try {
                const fetchedData = await uploadFile(file)
                console.log("successfully uploaded file", fetchedData)
            } catch (error) {
                console.log("error uploading file", error);
            }
        }
    }

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
          <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold">Upload a File</h2>
              <XIcon className="h-6 w-6 text-gray-400 cursor-pointer" />
            </div>

            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 flex flex-col items-center justify-center">
              {file ? (
                <div className="flex items-center justify-between w-full">
                  <div className="truncate text-gray-700">{file.name}</div>
                  <UploadIcon className="h-6 w-6 text-gray-400 cursor-pointer" />
                </div>
              ) : (
                <label
                  htmlFor="file-input"
                  className="cursor-pointer flex flex-col items-center justify-center"
                >
                  <UploadIcon className="h-12 w-12 text-gray-400 mb-2" />
                  <span className="text-gray-600">Drag and drop a file or click to upload</span>
                </label>
              )}
              <input
                id="file-input"
                type="file"
                className="hidden"
                onChange={handleFileChange}
              />
            </div>

            <button
              className="bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-md mt-6 w-full"
              onClick={handleFileUpload}
            >
              Upload
            </button>
          </div>
        </div>
      );
    };
export default FileUploadPage
