import React, {useEffect, useState} from 'react'
import { fetchTotalReturns } from '../services/API'
import ChartComponent from '../components/ChartComponent'
import 'bootstrap/dist/css/bootstrap.min.css';

interface totalReturns {
    ReferenceDate: string,
    CumulativeReturn: number,
}

function ReturnsPage(){
    const [totalReturns,setTotalReturns] = useState<totalReturns[]>([])
    const [isLoading, setIsLoading] = useState<boolean>(true);

    useEffect( () => {
        const handleFetch = async() => {
            try{
                setIsLoading(true)
                const receivedData = await fetchTotalReturns()
                setTotalReturns(receivedData.data)
            } catch(error) {
                console.log("error fetching data", error)
                throw error;
            } finally {
                setIsLoading(false)
            }
        }
        handleFetch()
    }, [])

    return (
        <div className="min-h-screen bg-blue-50 p-6">
            <div className="bg-white rounded-xl shadow-lg p-6 max-w-4xl mx-auto">
                {/* Dashboard Header */}
                <div className="flex justify-between items-center mb-6 border-b pb-4">
                    <div>
                        <h1 className="text-2xl font-semibold text-blue-800">Investment Returns</h1>
                        <p className="text-sm text-blue-500 mt-1">
                            {totalReturns.length > 0 && `Last updated: ${new Date(totalReturns[totalReturns.length - 1].ReferenceDate).toLocaleDateString()}`}
                        </p>
                    </div>
                    <div className="text-right">
                        <div className="text-sm font-medium text-blue-500">Total Return</div>
                        {totalReturns.length > 0 && (
                            <div className="text-xl font-semibold text-blue-900">
                                {(totalReturns[totalReturns.length - 1].CumulativeReturn * 100).toFixed(2)}%
                            </div>
                        )}
                    </div>
                </div>

                {/* Chart Section */}
                <div className="bg-gradient-to-r from-blue-50 to-blue-100 p-6 rounded-xl shadow-md">
                    <h2 className="text-xl font-semibold text-blue-800 mb-4">Investment Performance</h2>
                    {isLoading ? (
                        <div className="flex justify-center items-center h-[350px]">
                            <div className="text-blue-400">Loading data...</div>
                        </div>
                    ) : (
                        <div className="h-[350px] w-full bg-white rounded-xl shadow-md">
                            <ChartComponent ReturnsData={totalReturns} />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );

}

export default ReturnsPage
