import React, {useEffect, useState} from 'react'
import { fetchTotalReturns } from '../services/API'
import ChartComponent from '../components/ChartComponent'
import '../styles/ReturnsPage.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import bg from '../assets/bg.jpeg'

interface totalReturns {
    ReferenceDate: string,
    CumulativeReturn: number,
}

function ReturnsPage(){
    const [totalReturns,setTotalReturns] = useState<totalReturns[]>([])
    const [isLoading, setIsLoading] = useState<boolean>(true);

    useEffect( () => {
        const handleFetch = async() => {
            try{
                setIsLoading(true)
                const receivedData = await fetchTotalReturns()
                setTotalReturns(receivedData.data)
            } catch(error) {
                console.log("error fetching data", error)
                throw error;
            } finally {
                setIsLoading(false)
            }
        }
        handleFetch()
    }, [])

    return (
            <div className="min-vh-100 d-flex flex-column bg-light">
                <div className="container-fluid d-flex flex-column justify-content-center align-items-center p-4">
                <div className="chart-page-background" style={{backgroundImage: `url${bg}`}}>
                    <div className="bg-white rounded-3 shadow-lg p-4 w-100" style={{ maxWidth: "1200px", height: "100%" }}>
                        {/* Dashboard Header */}
                        <div className="d-flex justify-content-between align-items-center mb-4 border-bottom pb-3">
                            <div>
                                <h1 className="h3 text-primary">Investment Returns</h1>
                                <p className="text-muted small">
                                    {totalReturns.length > 0 && `Last updated: ${new Date(totalReturns[totalReturns.length - 1].ReferenceDate).toLocaleDateString()}`}
                                </p>
                            </div>
                            <div className="text-end">
                                <div className="text-muted small">Total Return</div>
                                {totalReturns.length > 0 && (
                                    <div className="fs-4 fw-bold text-primary">
                                        {(totalReturns[totalReturns.length - 1].CumulativeReturn * 100).toFixed(2)}%
                                    </div>
                                )}
                            </div>
                        </div>
                        </div>

                        {/* Chart Section */}
                        <div className="bg-light p-4 rounded-3 shadow-sm d-flex flex-column" style={{ height: "70%" }}>
                            <h2 className="h5 text-primary mb-3">Investment Performance</h2>
                            {isLoading ? (
                                <div className="d-flex justify-content-center align-items-center" style={{ height: "100%" }}>
                                    <div className="text-muted">Loading data...</div>
                                </div>
                            ) : (
                                <div className="bg-white rounded-3 shadow-sm" style={{ height: "100%" }}>
                                    <ChartComponent ReturnsData={totalReturns} />
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
    );
}

export default ReturnsPage
