import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const SPReturnsChart = ({ ReturnsData = [] }) => {
  // Ensure we have valid data
  const validData = Array.isArray(ReturnsData) ? ReturnsData : [];

  const chartData = {
    labels: validData.map(item => item?.Ref || ''),
    datasets: [
      {
        label: 'S&P 500 Returns',
        data: validData.map(item => item?.return || 0),
        fill: true,
        borderColor: '#10b981',
        backgroundColor: 'rgba(16, 185, 129, 0.1)',
        tension: 0.4,
        pointRadius: 0,
        pointHoverRadius: 4,
        pointHoverBackgroundColor: '#10b981',
        pointHoverBorderColor: '#fff',
        pointHoverBorderWidth: 2,
      }
    ]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        backgroundColor: 'white',
        titleColor: '#374151',
        bodyColor: '#10b981',
        borderColor: '#e5e7eb',
        borderWidth: 1,
        padding: 12,
        titleFont: {
          size: 14,
          weight: '600'
        },
        bodyFont: {
          size: 13,
          weight: '500'
        },
        callbacks: {
          label: function(context) {
            return `Return: ${context.parsed.y.toFixed(2)}%`;
          }
        }
      }
    },
    scales: {
      x: {
        grid: {
          display: false
        },
        ticks: {
          color: '#6b7280',
          font: {
            size: 12
          }
        },
        border: {
          display: false
        }
      },
      y: {
        grid: {
          color: '#f3f4f6'
        },
        ticks: {
          color: '#6b7280',
          font: {
            size: 12
          },
          callback: function(value) {
            return value + '%';
          }
        },
        border: {
          display: false
        }
      }
    },
    interaction: {
      intersect: false,
      mode: 'index'
    },
    elements: {
      line: {
        borderWidth: 2
      }
    }
  };

  return (
    <div className="w-full max-w-4xl p-6 bg-white rounded-lg shadow-sm">
      <div className="mb-6">
        <h2 className="text-xl font-semibold text-gray-800">S&P 500 Total Returns</h2>
        <p className="text-sm text-gray-500">Historical performance tracking</p>
      </div>

      <div className="h-64 w-full">
        <Line data={chartData} options={options} />
      </div>

      {validData.length > 0 && (
        <div className="grid grid-cols-3 gap-4 pt-4 mt-4 border-t">
          <div>
            <p className="text-sm text-gray-500">Latest Return</p>
            <p className="text-2xl font-semibold text-emerald-600">
              {(validData[validData.length - 1]?.return || 0).toFixed(2)}%
            </p>
          </div>
          <div>
            <p className="text-sm text-gray-500">Peak Return</p>
            <p className="text-2xl font-semibold">
              {Math.max(...validData.map(item => item?.return || 0)).toFixed(2)}%
            </p>
          </div>
          <div>
            <p className="text-sm text-gray-500">Average Return</p>
            <p className="text-2xl font-semibold">
              {(validData.reduce((acc, curr) => acc + (curr?.return || 0), 0) / (validData.length || 1)).toFixed(2)}%
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SPReturnsChart;
