import React, {useState,use} from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import FileUploadPage from './pages/Upload'
import ReturnsPage from './pages/Returns';
import { elements } from 'chart.js';

function App() {
  const [fileUploaded, setFileUploaded] = useState(false)

  useEffect(() => {
    const storedFileUploaded = sessionStorage.getItem('fileUploaded') === 'true';
    setFileUploaded(storedFileUploaded);
  }, []);

  const handleFileUploadSuccess = () => {
    setFileUploaded(true);
    sessionStorage.setItem('fileUploaded', 'true'); // Persist the state in sessionStorage
  };


  return (
    <Router>
        <Routes>
            <Route path='/' element={<FileUploadPage onFileUpload={handleFileUploadSuccess} />} />
            <Route path="/protected" element={fileUploaded ? <ReturnsPage /> : <Navigate to="/" />} />
        </Routes>
    </Router>
  )

}

export default App;
