import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './ChartComponent.css';  // Import the CSS file

interface TotalReturns {
  ReferenceDate: string;
  CumulativeReturn: number;
}

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

interface Chart {
  labels: string[];
  datasets: {
    label: string;
    data: number[];
    borderColor: string;
    backgroundColor: string;
    fill: boolean;
  }[];
}

function ChartComponent({ ReturnsData }: { ReturnsData: TotalReturns[] }) {
  const [chartData, setChartData] = useState<Chart>({
    labels: [],
    datasets: [
      {
        label: '',
        data: [],
        borderColor: '',
        backgroundColor: '',
        fill: false,
      },
    ],
  });

  const [averageReturn, setAverageReturn] = useState<number | null>(null);
  const [latestReturn, setLatestReturn] = useState<number | null>(null);
  const [maxReturn, setMaxReturn] = useState<number | null>(null);

  useEffect(() => {
    const refDate = ReturnsData.map((item) => item.ReferenceDate);
    const cumulativeRets = ReturnsData.map((item) => item.CumulativeReturn);

    const avgReturn = cumulativeRets.reduce((acc, val) => acc + val, 0) / cumulativeRets.length;
    const latest = cumulativeRets[cumulativeRets.length - 1];
    const max = Math.max(...cumulativeRets);

    setChartData({
      labels: refDate,
      datasets: [
        {
          label: 'Cumulative Return',
          data: cumulativeRets,
          borderColor: 'rgba(75, 192, 192, 1)',
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          fill: true,
        },
      ],
    });

    setAverageReturn(avgReturn);
    setLatestReturn(latest);
    setMaxReturn(max);
  }, [ReturnsData]);

  return (
    <div className="chart-container">
      <Line data={chartData} options={styles.chartOptions} />
      <div className="stats-container">
        <div className="stat-item">
          <strong>Average Return: </strong>
          {averageReturn !== null ? averageReturn.toFixed(2) : 'N/A'}%
        </div>
        <div className="stat-item">
          <strong>Latest Return: </strong>
          {latestReturn !== null ? latestReturn.toFixed(2) : 'N/A'}%
        </div>
        <div className="stat-item">
          <strong>Max Return: </strong>
          {maxReturn !== null ? maxReturn.toFixed(2) : 'N/A'}%
        </div>
      </div>
    </div>
  );
}

const chartOptions = {
  chartOptions: {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        callbacks: {
          label: (context: any) => `${context.dataset.label}: ${context.raw}%`,
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Date',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Cumulative Return (%)',
        },
      },
    },
  },
};

export default ChartComponent;
